#!/usr/bin/env pwsh
$basedir=Split-Path $MyInvocation.MyCommand.Definition -Parent

$exe=""
$pathsep=":"
$env_node_path=$env:NODE_PATH
$new_node_path="C:\Users\Administrator\Desktop\moltbot-main\node_modules\.pnpm\node-downloader-helper@2.1.10\node_modules\node-downloader-helper\bin\node_modules;C:\Users\Administrator\Desktop\moltbot-main\node_modules\.pnpm\node-downloader-helper@2.1.10\node_modules\node-downloader-helper\node_modules;C:\Users\Administrator\Desktop\moltbot-main\node_modules\.pnpm\node-downloader-helper@2.1.10\node_modules;C:\Users\Administrator\Desktop\moltbot-main\node_modules\.pnpm\node_modules"
if ($PSVersionTable.PSVersion -lt "6.0" -or $IsWindows) {
  # Fix case when both the Windows and Linux builds of Node
  # are installed in the same directory
  $exe=".exe"
  $pathsep=";"
} else {
  $new_node_path="/mnt/c/Users/Administrator/Desktop/moltbot-main/node_modules/.pnpm/node-downloader-helper@2.1.10/node_modules/node-downloader-helper/bin/node_modules:/mnt/c/Users/Administrator/Desktop/moltbot-main/node_modules/.pnpm/node-downloader-helper@2.1.10/node_modules/node-downloader-helper/node_modules:/mnt/c/Users/Administrator/Desktop/moltbot-main/node_modules/.pnpm/node-downloader-helper@2.1.10/node_modules:/mnt/c/Users/Administrator/Desktop/moltbot-main/node_modules/.pnpm/node_modules"
}
if ([string]::IsNullOrEmpty($env_node_path)) {
  $env:NODE_PATH=$new_node_path
} else {
  $env:NODE_PATH="$new_node_path$pathsep$env_node_path"
}

$ret=0
if (Test-Path "$basedir/node$exe") {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "$basedir/node$exe"  "$basedir/../node-downloader-helper/bin/ndh" $args
  } else {
    & "$basedir/node$exe"  "$basedir/../node-downloader-helper/bin/ndh" $args
  }
  $ret=$LASTEXITCODE
} else {
  # Support pipeline input
  if ($MyInvocation.ExpectingInput) {
    $input | & "node$exe"  "$basedir/../node-downloader-helper/bin/ndh" $args
  } else {
    & "node$exe"  "$basedir/../node-downloader-helper/bin/ndh" $args
  }
  $ret=$LASTEXITCODE
}
$env:NODE_PATH=$env_node_path
exit $ret
